import { Card, Image, List, Select, Skeleton, Space, Tag, Typography } from 'https://cdn.jsdelivr.net/npm/antd@5.11.1/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import React from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';

function App() {
	const [rssItems, setRssItems] = React.useState([]);
	const [loading, setLoading] = React.useState(true);
	const [error, setError] = React.useState(null);
	const [category, setCategory] = React.useState('phong-chong-thien-tai');

	const categories = {
		'phong-chong-thien-tai': 'Phòng chống thiên tai',
		'khi-hau': 'Khí hậu',
		'moi-truong': 'Môi trường',
	};

	React.useEffect(() => {
		setLoading(true);
		const proxyUrl = 'https://api.allorigins.win/get?url=';
		const rssUrl = encodeURIComponent(`https://kinhtemoitruong.vn/rss/moi-truong-xanh/${category}.rss`);

		fetch(`${proxyUrl}${rssUrl}`)
			.then(response => response.json())
			.then(data => {
			const parser = new DOMParser();
			const xmlDoc = parser.parseFromString(data.contents, "text/xml");
			const items = Array.from(xmlDoc.querySelectorAll("item")).map(item => {
				const description = item.querySelector("description")?.textContent || '';
				const imgMatch = description.match(/<img src="([^"]+)"/);
				const descText = description.replace(/<[^>]+>/g, '').trim();

				// Convert image URL to high resolution version
				const imageUrl = imgMatch ? imgMatch[1].replace('thumb_x200x', 'thumb_x550x') : null;

				return {
					title: item.querySelector("title")?.textContent.replace(/<!\[CDATA\[|\]\]>/g, '').trim() || '',
					link: item.querySelector("link")?.textContent || '',
					description: descText,
					image: imageUrl,
					pubDate: item.querySelector("pubDate")?.textContent || '',
				};
			});
			setRssItems(items);
			setLoading(false);
		})
			.catch(err => {
			setError('Failed to load RSS feed');
			setLoading(false);
		});
	}, [category]);

	return (
		<Card className="app">
		<Space direction="vertical" size="large" style={{ width: '100%' }}>
		<Typography.Title level={2}>
			<Space>
			<img 
src="https://ktmt.vnmediacdn.com/static/images/share-logo.jpg" 
alt="Logo"
style={{ height: 40, marginRight: 10 }}
/>
<Select
value={category}
onChange={setCategory}
style={{ width: 200 }}
options={Object.entries(categories).map(([key, label]) => ({
	value: key,
	label: label
}))}
/>
<Tag color="green">Kinh tế Môi trường</Tag>
</Space>
</Typography.Title>

<Skeleton loading={loading} active paragraph={{ rows: 4 }}>
	{error ? (
	 <Card type="inner" className="error">
	 <Typography.Text type="danger">{error}</Typography.Text>
</Card>
) : (
	<List
	itemLayout="vertical"
	size="large"
	dataSource={rssItems}
renderItem={(item) => (
	<List.Item>
	<Card type="inner" hoverable>
	<Space direction="vertical" size="large" style={{ width: '100%' }}>
	{item.image && (
	 <Image
	 src={item.image}
alt={item.title}
style={{ maxWidth: '100%', borderRadius: 8 }}
/>
)}
	<div>
		<Typography.Title level={4}>
			<a href={item.link} target="_blank" rel="noopener noreferrer">
				{item.title}
</a>
</Typography.Title>
<Typography.Paragraph>{item.description}</Typography.Paragraph>
<Typography.Text type="secondary">
	{new Date(item.pubDate).toLocaleString('vi-VN')}
		</Typography.Text>
</div>
</Space>
</Card>
</List.Item>
)}
/>
)}
	</Skeleton>
</Space>
</Card>
);
}

