/* .app - Đảm bảo nó chiếm toàn bộ màn hình và bản đồ có thể nằm dưới các phần tử khác */
.app {
  position: relative; /* Cần position relative để các phần tử con như search-bar có thể định vị tuyệt đối */
  top: 0;
  left: 0;
  width: 100vw; /* Chiếm toàn bộ chiều rộng màn hình */
  height: 100vh; /* Chiếm toàn bộ chiều cao màn hình */
  display: flex;
  justify-content: center;
  align-items: center;
  border-radius: 0px;
  border: none;
}

.fas {
  padding-right: 3px;
}

/* Tạo không gian cho map */
#map {
  position: absolute; /* Đảm bảo map không bị chặn */
  top: 0;
  left: 0;
  width: 100%;
  height: 100%; /* Đảm bảo bản đồ chiếm toàn bộ màn hình */
  z-index: 0; /* Đặt bản đồ dưới search-bar */
}

/* .search-bar - Đảm bảo nó nằm trên bản đồ */
.search-bar {
  position: absolute;
  top: 10px; /* Khoảng cách từ trên xuống */
  left: 50%;
  transform: translateX(-50%); /* Căn giữa chính xác */
  background: rgba(255, 255, 255, 0.7); /* Nền trong suốt với một chút màu trắng */
  padding: 10px;
  border-radius: 10px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 300px; /* Chiều rộng của search-bar */
  z-index: 1; /* Đảm bảo search-bar nằm trên bản đồ */
}

/* Các phần tử trong search-bar */
.search-bar input {
  flex-grow: 1; /* Chiếm toàn bộ không gian còn lại */
  margin-left: 10px;
  padding: 5px;
  border: 1px solid #ccc;
  border-radius: 5px;
}

.search-bar button {
  background-color: transparent;
  border: none;
  color: #333;
  cursor: pointer;
  padding: 5px;
}

.search-bar i {
  font-size: 18px;
}

.type-dropdown {
  display: none;
  position: absolute;
  background-color: #f9f9f9;
  min-width: 160px;
  box-shadow: 0px 8px 16px 0px rgba(0, 0, 0, 0.2);
  z-index: 1;
  right: 40px;
  top: 40px;
}

.type-dropdown a {
  color: black;
  padding: 12px 16px;
  text-decoration: none;
  display: block;
}

.type-dropdown a:hover {
  background-color: #f1f1f1;
}

.show {
  display: block;
}

.data-num {
  font-size: 1rem;
}

