import React, { useEffect, useRef } from 'https://cdn.jsdelivr.net/npm/react@18.2.0/+esm';
import reactDom from 'https://cdn.jsdelivr.net/npm/react-dom@18.2.0/+esm';
import goong_js from 'https://cdn.jsdelivr.net/npm/@goongmaps/goong-js@1.0.9/+esm';
const GoongMap = () => {
  const mapContainer = useRef(null);
  useEffect(() => {
    // Add this at the start of your script
    let globalGeojsonData = null;
    let globalCircleFeatures = null;

    // Set your goong mapbox access token
    goong_js.accessToken = 'mQn3EXqA6N5UrnTUr72TgItlg0yzPHxavSEmK2JE';
    var map = new goong_js.Map({
      container: 'root',
      style: 'https://tiles.goong.io/assets/goong_map_web.json',
      // stylesheet location
      center: [108.212, 16.068],
      // starting position [lng, lat]
      zoom: 5 // starting zoom
    });
    //nút zoom
    var zoom = new goong_js.NavigationControl({
      showCompass: false // Ẩn button la bàn
    });
    //nút định vị
    var getLocal = new goong_js.GeolocateControl({
      positionOptions: {
        enableHighAccuracy: true,
        // Bật chế độ định vị với độ chính xác cao
        timeout: 6000
      },
      trackUserLocation: true,
      // Hiển thị vị trí người dùng liên tục nghĩa là họ di chuyển cũng sẽ hiển thị
      showUserLocation: true
    });
    map.addControl(getLocal, 'bottom-right');
    map.addControl(zoom, 'bottom-right');
    var geoCoder = new GoongGeocoder({
      accessToken: 'your_api_token',
      goong_js: goong_js
    });

    // Add the control to the map.
    //map.addControl(geoCoder);

    // Modify the search input handler
    document.getElementById('searchInput').addEventListener('input', function (e) {
      if (!globalGeojsonData) return; // Guard clause if data isn't loaded yet

      // console.log(e);
      const searchTerm = e?.target?.value?.toLowerCase();

      // Filter features based on place_type
      const filteredFeatures = globalGeojsonData.features.filter(feature => feature.properties?.place_type?.toLowerCase()?.includes(searchTerm));

      // Filter circle features to match
      const filteredCircles = globalCircleFeatures.filter(circle => circle.properties?.place_type?.toLowerCase()?.includes(searchTerm));

      // Update points on map
      map.getSource('places').setData({
        type: 'FeatureCollection',
        features: searchTerm ? filteredFeatures : globalGeojsonData.features
      });

      // Update circles on map
      map.getSource('circles').setData({
        type: 'FeatureCollection',
        features: searchTerm ? filteredCircles : globalCircleFeatures
      });
    });
    map.on('load', function () {
      // Add circle creation helper function at the start
      function createCircle(center, radiusInMeters) {
        const points = 64;
        const coords = [];
        for (let i = 0; i < points; i++) {
          const angle = i / points * (2 * Math.PI);
          const radius = radiusInMeters / 111300; // convert meters to degrees
          coords.push([center[0] + radius * Math.cos(angle), center[1] + radius * Math.sin(angle)]);
        }
        coords.push(coords[0]); // close the circle
        return coords;
      }
      fetch('https://zwdaqmfqjaccijhrkbfl.supabase.co/rest/v1/place', {
        method: "GET",
        headers: {
          'apikey': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Inp3ZGFxbWZxamFjY2lqaHJrYmZsIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzI5NDEyNzIsImV4cCI6MjA0ODUxNzI3Mn0.bpDrWBpWfkf4yfeUJUFq4mdHE145BPolSOyoLbk2ZPM',
          'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6Inp3ZGFxbWZxamFjY2lqaHJrYmZsIiwicm9sZSI6ImFub24iLCJpYXQiOjE3MzI5NDEyNzIsImV4cCI6MjA0ODUxNzI3Mn0.bpDrWBpWfkf4yfeUJUFq4mdHE145BPolSOyoLbk2ZPM',
          'Content-Type': 'application/json'
        }
      }).then(response => response.json()).then(data => {
        // Store the converted data globally
        globalGeojsonData = {
          type: 'FeatureCollection',
          features: data.map(place => ({
            type: 'Feature',
            geometry: {
              type: 'Point',
              coordinates: [place.longitude, place.lattitude] // Dùng tọa độ longitude, latitude
            },
            properties: {
              id: place.id,
              place_type: place.place_type,
              radius: place.radius,
              location: place.location,
              additional_info: place.additional_info
            }
          }))
        };

        // Store circle features globally
        globalCircleFeatures = data.map(place => ({
          type: 'Feature',
          geometry: {
            type: 'Polygon',
            coordinates: [createCircle([place.longitude, place.lattitude], place.radius || 100)]
          },
          properties: {
            id: place.id,
            place_type: place.place_type
          }
        }));

        // Initialize sources with full data
        map.addSource('places', {
          type: 'geojson',
          data: globalGeojsonData,
          cluster: true,
          clusterMaxZoom: 14,
          clusterRadius: 50
        });

        // Add circles source with all circles
        map.addSource('circles', {
          type: 'geojson',
          data: {
            type: 'FeatureCollection',
            features: globalCircleFeatures
          }
        });
        console.log(globalGeojsonData);

        // Các thao tác thêm layer sau khi thêm nguồn dữ liệu
        map.addLayer({
          id: 'clusters',
          type: 'circle',
          source: 'places',
          filter: ['has', 'point_count'],
          // Use step expressions (https://docs.goong.io/style-spec/#expressions-step)
          // with three steps to implement three types of circles:
          //   * Blue, 20px circles when point count is less than 100
          //   * Yellow, 30px circles when point count is between 100 and 750
          //   * Pink, 40px circles when point count is greater than or equal to 750

          paint: {
            'circle-color': ['step', ['get', 'point_count'], '#51bbd6', 50, '#f1f075', 100, '#f28cb1'],
            'circle-radius': ['step', ['get', 'point_count'], 20, 100, 30, 750, 40]
          }
        });
        map.addLayer({
          id: 'cluster-count',
          type: 'symbol',
          source: 'places',
          filter: ['has', 'point_count'],
          layout: {
            'text-field': '{point_count_abbreviated}',
            'text-font': ['Roboto Regular'],
            'text-size': 12
          }
        });
        map.addLayer({
          id: 'unclustered-point',
          type: 'circle',
          source: 'places',
          filter: ['!', ['has', 'point_count']],
          paint: {
            'circle-color': ['match', ['get', 'place_type'], 'an toàn', '#28a745',
            // Màu xanh cho 'an toàn'
            'cứu trợ', '#ffc107',
            // Màu vàng cho 'cứu trợ'
            'thiên tai', '#dc3545',
            // Màu đỏ cho 'thiên tai'
            'dịch bệnh', '#FF00FF',
            // Màu đỏ cho 'thiên tai'
            '#6c757d' // Màu xám cho các loại khác
            ],
            'circle-radius': 6,
            'circle-stroke-width': 1,
            'circle-stroke-color': '#fff'
          }
        });
        map.addLayer({
          id: 'circle-fill',
          type: 'fill',
          source: 'circles',
          paint: {
            'fill-color': '#088',
            'fill-opacity': 0.2
          }
        });
        map.addLayer({
          id: 'circle-outline',
          type: 'line',
          source: 'circles',
          paint: {
            'line-color': '#088',
            'line-width': 2
          }
        });

        //Truy cập vào một cụm (cluster) để xem dữ liệu chi tiết hơn
        // inspect a cluster on click
        map.on('click', 'clusters', function (e) {
          var features = map.queryRenderedFeatures(e.point, {
            layers: ['clusters']
          });
