						.addTo(map);
					map.on('mouseenter', 'clusters', function () {
						map.getCanvas().style.cursor = 'pointer';
					});
					map.on('mouseleave', 'clusters', function () {
						map.getCanvas().style.cursor = '';
					});


				});
				// Get unique place types
				const placeTypes = [...new Set(data.map(place => place.place_type))];

				// Populate dropdown
				const typeDropdown = document.getElementById('typeDropdown');
				placeTypes.forEach(type => {
					const link = document.createElement('a');
					link.href = '#';
					link.textContent = type;
					link.onclick = function (e) {
						e.preventDefault();
						document.getElementById('searchInput').value = type;
						// Trigger the input event to update the map
						document.getElementById('searchInput').dispatchEvent(new Event('input'));
						typeDropdown.classList.remove('show');
					};
					typeDropdown.appendChild(link);
				});

				// Add click handler for type button
				document.getElementById('typeButton').onclick = function () {
					typeDropdown.classList.toggle('show');
				};

				// Close dropdown when clicking outside
				window.onclick = function (event) {
					if (!event.target.matches('.btn-icon') && !event.target.matches('.fas.fa-info-circle')) {
						if (typeDropdown.classList.contains('show')) {
							typeDropdown.classList.remove('show');
						}
					}
				};


			})
				.catch(error => {
				console.log('Error fetching data:', error);
			});

		});

	}, []); // Empty dependency array ensures this effect runs once after the first render

	return (
		<div ref={mapContainer} style={{ width: '100%', height: '100%' }} />
);
};

